import { Component, OnInit } from '@angular/core';
import { DatePipe } from '@angular/common';
import { IFormula } from './app.formulas.formula-interface';
import { ApiService } from '../services/api.service';
import { Router, ActivatedRoute} from '@angular/router'
import { FormSettings } from '../interfaces/form-settings'
import jspdf from 'jspdf';

@Component( {
	selector:'formula-form',
	templateUrl:'formula-form.html',
	styleUrls:['formulas.css'],
	providers:[ApiService,DatePipe],
	inputs:['formSettings','formula']
} )
export class FormulaFormComponent implements OnInit{
	formSettings:FormSettings = {formType:'',title:''};
	materials:any = [];
	instructions = []; 
	id;
	pdf = {x:null,y:null}
	formula:IFormula = {
		number:'',
		name:'',
		revision_no:1,
		total_weight:0,
		weight_uom:'LBS',
		yield:0,
		loss_factor:4,
		yield_uom:'LBS',
		status:'',
		materials:[],
		instructions:[]
	};
	
	constructor (private apiService:ApiService, private router:Router,private activatedRoute:ActivatedRoute,private datePipe:DatePipe) {
		let setId = this.activatedRoute.params.subscribe(params=>{
			let id = +params['id'];
			this.id = id;
			console.log('id: ',id);		
		});	
		if(setId){
			this.formSettings.formType = 'update';
			this.formSettings.title = 'Edit Formula';
		}else{
			this.formSettings.formType = 'new';
			this.formSettings.title = 'New Formula'
		}
	}

	ngOnInit(){
		if(this.id > 0){
			this.setFormula();
			this.calculateMaterialsForTotalWeight();
		}
	}

	setFormula(){
		this.apiService.getFormulaWithId(this.id).subscribe( (data) => {
			this.formula = data.json()
			this.formula.instructions.push({step:''});
			console.log(data.json());
		},
		( error ) => {
			console.log(error) 
		},
		()=> { 
			console.log("complete");
		});
	}

	calculateMaterialsForTotalWeight(){
		this.formula.total_weight = this.formula.materials.reduce( (total,current) => total+parseFloat(current.weight),0);
		this.formula.yield = this.formula.total_weight - (this.formula.total_weight * (this.formula.loss_factor / 100));
	}

	materialSelected ( material ){
		if (material.id){
			material['material_id'] = material.id;
		}
		
		material['formula_id']=this.formula.id;
		material.id = null;
		material.weight_unit = material.weight_unit||'LBS';
		material.lbs = parseFloat(material.lbs)||0;
		material.total_weight = material.lbs;
		this.formula.materials.push(material);

		this.calculateMaterialsForTotalWeight();
	}

	removeMaterialItem ( i ){
		this.formula.materials.splice ( i ,1 );
		this.calculateMaterialsForTotalWeight();
	}

	uom ( uom,material ){
		material.weight_unit = uom;
	}

	addStep (step_no) {
		this.formula.instructions.push({step:''})
	}   

	reNumber(){
		this.formula.instructions.map( (data, i) => this.formula.instructions[i]['step_no'] = i+1 );
	}

	deleteStep (index) {
		this.formula.instructions.splice(index,1);
		this.reNumber()
	}

	submit(formula){
		if(this.formSettings.formType==='new'){
			this.save(formula);
		}else{
			this.update(formula);
		}
	}

	save(formula){
		this.reNumber()
		this.apiService.postFormula(formula).subscribe();
		this.router.navigate(['/formulas']);
		console.log(formula);
	}

	update(formula){
		this.reNumber();
		this.apiService.updateFormula(formula).subscribe();
		this.router.navigate(['/formulas']);
		console.log(this.formula)
	}

	updateMaterial(material,i,key,value){
		material[key] = value;
	}

	updateStep( event , step ){
		console.log('update: ',event,step);
		step.step = event.value;
		step.step_no = event.step_no;
	}

	formulaTicket(formula){
		let doc = new jspdf({unit:"in", format:[8.5,11]});
		
		const logo1 = 'data:image/png;base64,';

		doc.addImage(logo1,'PNG',0,0,8.5,1.32);
		// BATCH TICKET FORM TITLE
		doc.text(`FORMULA REVISION # ${formula.revision_no}`,4.25,.5,null,null,'center');
		
		// Product Title
		let title = '[ '+formula.number+' ] '+formula.name;
		doc.setFontStyle('bold')
		.text(title,4.25,1,null,null,'center');

		// formula Category Letter
		doc.setFontSize('80')
		.setFont('georgia')
		.text("F",4.25,2,null,null,'center');

		// Formula #
		doc.setFontSize('12')
		.setFont('Arial')
		.setFontStyle('normal');
		
		doc.text('Formula #: ',1.625,2.25,null,null,'right');
		doc.text(formula.number,1.75,2.25);

		doc.text('Last Revised: ',1.625,2.5,null,null,'right');
		doc.text(this.datePipe.transform(formula.updated,'shortDate'),1.75,2.5);

		doc.text("Total Weight: ",7,2.25,null,null,'right');
		doc.text(`${formula.total_weight} ${formula.weight_uom}`,8,2.25,null,null,'right');

		doc.text("Yield: ",7,2.5,null,null,'right');
		doc.text(`${formula.yield} ${formula.yield_uom}`,8,2.5,null,null,'right');
		
		this.ticketSection(doc,'MATERIALS',4.25,2.75,null,null,'center');
		this.ticketMaterials(doc);

		this.ticketSection(doc,'INSTRUCTIONS',4.25,this.pdf.y+.25,null,null,'center');
		this.ticketInstructions(doc);
		// Save 
		doc.save('FormulaTicket'+formula.name +'.pdf');
		

		console.log(formula,doc);
	}

	ticketMaterials(doc){
		doc.setTextColor(0,0,0);
		doc.setFontSize(9);
		doc.setLineWidth(.009);
		doc.setDrawColor(.50);
		doc.text('Item#',.875,3.375,null,null,'center');
		doc.text('Description',4.25,3.375,null,null,'center');
		doc.text('Weight',7,3.375,null,null,'center');
		doc.text('UOM',7.875,3.375,null,null,'right');
		let y=3.5;

		this.formula.materials.map( data => { 
			doc.setDrawColor(135,226,209); 
			doc.rect(.5,y,7.5,.25,'D');
			doc.text(data.item_no,.875,y+.19,null,null,'center');
			doc.text(data.name,1.5,y+.19,null,null);
			doc.text(data.weight,7,y+.20,null,null,'center');
			doc.text(data.weight_unit,7.865,y+.19,null,null,'right');
			y = y+.25;
			this.pdf.y = y;
			return doc;
		});	
	}

	ticketSection(doc,title,x,y,flags,angle,align){
		doc.setFillColor(135,226,209);
		doc.rect(.5,y,7.5,.30,'F');
		doc.setFontSize(20)
		doc.setTextColor(0,0,0);
		doc.text(title,x,(y+.30)-.045,null,null,align);
		this.pdf.y = y;
		return doc;
	}

	ticketInstructions(doc){
		doc.setTextColor(0,0,0);
		doc.setFontSize(9);
		doc.setLineWidth(.009);
		doc.setDrawColor(.50);

		let y = this.pdf.y+.5;

		this.formula.instructions.map( data => {
			doc.setDrawColor(135,226,209); 
			if (data.step_no!=undefined){
				doc.rect(.5,y,7.5,.25,'D');
				doc.text(`Step# ${data.step_no}`,.875,y+.19,null,null,'center');
				doc.text(data.step,1.5,y+.19,null,null);
				y = y+.25;
				this.pdf.y = y;
				
				return doc;
			}
		});	
	}

}










































































